            var ys = y + stretchOffset;

            var xsb = FastFloor(xs);
            var ysb = FastFloor(ys);

            var xins = xs - xsb;
            var yins = ys - ysb;

            var inSum = xins + yins;

            var squishOffsetIns = inSum * SQUISH_2D;
            var dx0 = xins + squishOffsetIns;
            var dy0 = yins + squishOffsetIns;

            var hash =
               (int)(xins - yins + 1) |
               (int)(inSum) << 1 |
               (int)(inSum + yins) << 2 |
               (int)(inSum + xins) << 4;

            var c = lookup2D[hash];

            var value = 0.0;
            while (c != null)
            {
